let inputDiv = document.getElementById('input');

let input = inputDiv.textContent;

input = ''

/********************************* PART ONE *********************************/

let stringArray = input.split("");

let signal = [];

let currentLetter = stringArray.shift();

let currentIndex = 0;

while(stringArray){
    if (signal.includes(currentLetter)) {
       signal = removeDuplicate(signal, currentLetter);
    } 
    signal.push(currentLetter);
    if (signal.length == 4){

        break;
    }
    currentLetter = stringArray.shift();
    currentIndex++;
}

function removeDuplicate(array, letter){
    for (let i = 0 ; i < array.length ; i++){
        if ( array[i].charCodeAt(0) === letter.charCodeAt(0)){
            return array.slice(i+1);
        }
    }
}

console.log('Signal at: ' + `${currentIndex+1}`);

